mapper.select:

select mem_id, mem_password , mem_name, mem_state
from member
where mem_id = #{mem_id} and mem_password = #{mem_password}

select mem_id, mem_cellphone, mem_name
from member
where mem_name = #{mem_name} and mem_cellphone = #{mem_cellphone}

mapper.update:
update member
set mem_password = #{mem_password}
where mem_id = #{mem_id}

mapper.select:
select mem_password
from member
where mem_id = #{mem_id}
