mapper.select:
SELECT mem_id, mem_name, mem_gender, mem_birthday, mem_cellphone, mem_state FROM ( SELECT list.*, ROWNUM AS RNUM FROM (
ORDER BY mem_id ASC ) list )
RNUM BETWEEN #{start_row} AND #{end_row}


SELECT NVL(count(1), 0) FROM
( SELECT list.*, ROWNUM AS RNUM
FROM(

ORDER BY mem_id desc
) list
)

mapper.update:
UPDATE member
SET
mem_state =
#{mem_state}
