mapper.select:

select inq_number, mem_id, mem_name,
inq_title, inq_regdate, inq_state
from
( SELECT list.*, ROWNUM AS RNUM
FROM (

ORDER BY inq_number ASC
) list
)



			RNUM BETWEEN #{start_row} AND #{end_row}


SELECT NVL(count(1), 0) FROM
( SELECT list.*, ROWNUM AS RNUM
FROM(

ORDER BY inq_number desc
) list
)
